import React ,{useState} from "react";
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import Zoom from "@material-ui/core/Zoom";


function CreateArea(props){
    const [isExpanded,setExpanded]=useState(false);
    const [note,setNote]=useState({
       title:"",
       content:""
    });
    function handleChange(event){
        const{name,value}=event.target;
        setNote(prevnote => {
            return {
                ...prevnote,
                [name]:value
            };
        });
    }
    function submitNote(event){
        props.onAdd(note);
        setNote({title :"" , content : ""})
        event.preventDefault();

    }
    function expand(){
        setExpanded(true);
    }
    return(
        <div>
            <form className="create-note">
                {isExpanded ? <input name="title" onChange={handleChange} value={note.title} placeholder="Title" autoComplete="off"/> : null}
                <textarea name="content" onClick={expand} onChange={handleChange} value={note.content} placeholder="Take a note..." rows={isExpanded ? 3:1} autoComplete="off"/>
                <Zoom in={isExpanded}>
                <Fab onClick={submitNote}>
                    <AddIcon />
                </Fab>
                </Zoom>
            </form>
        </div>
    );
}
export default CreateArea;
